<?php//Change these variables to the ip address and port of your arduino$host = '192.168.1.166';$port = 23;echo "You have submitted something";//The package vaiable is what gets sent to the arduinoif ($_POST["status"] == "on"){$package = "1";}else{$package = "0";}//The rest of this code sends the package variable to the arduino$timeout =1;$socket  = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);socket_set_option($socket, SOL_SOCKET, SO_RCVTIMEO, array('sec' => $timeout, 'usec' => 0));socket_connect($socket, $host, $port);$ts = microtime(true);socket_send($socket, $package, strLen($package), 0);socket_close($socket);//LEFTOVERS/*if ($socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP) === false) {    echo "socket_create() failed: reason: " . socket_strerror(socket_last_error()) . "\n";} else {    echo "OK.\n";}*/	?>